import { IJsonConvertable } from '../ref';
import { ConditionType } from './ConditionType';
/**
 * 条件实体接口。
 */
export interface IConditionEntity extends IJsonConvertable {
    /**
     * 条件标识。
     */
    id: string;
    /**
     * 是否启用状态。
     */
    isEnabled: boolean;
    /**
     * 名称。
     */
    name: string;
    /**
     * 类型。
     */
    type: ConditionType;
    /**
     * 内容。
     */
    content: string;
    /**
     * 备注。
     */
    remarks: string;
    supportAutoProcess: boolean;
    supportPushProcess: boolean;
    supportPullProcess: boolean;
}
