import { ISlimEntity } from '../ref';
/**
 * 单据流简化实体接口。
 */
export interface ISlimBillProcessEntity extends ISlimEntity {
    /**
     * 单据流编号。
     */
    code: string;
    /**
     * 上游单据视图对象元数据标识。
     */
    upBillVoMetadataId: string;
    /**
     * 上游单据视图对象标识。
     */
    upBillVoId: string;
    /**
     * 下游单据视图对象元数据标识。
     */
    downBillVoMetadataId: string;
    /**
     * 下游单据视图对象标识。
     */
    downBillVoId: string;
    /**
     * 是否支持自动流状态。
     */
    isSupportAutomatinFlow: boolean;
    /**
     * 业务事件标识。
     */
    bizEventId: string;
    /**
     * 是否系统预置条件。
     */
    sysInit: boolean;
    /**
     * 父流程标识。
     */
    parentId: string;
    /**
     * 是否启用父流程自动生单触发条件。
     */
    enabledParentEventRouterCond: boolean;
    /**
     * 是否启用父流程转换规则执行条件。
     */
    enabledParentMappingCond: boolean;
    /**
     * 上游单据业务实体标识。
     */
    upBillBeId: string;
    /**
     * 下游单据业务实体标识。
     */
    downBillBeId: string;
    /**
     * 是否是源头流程。
     */
    origin: boolean;
}
