import { NotifyService } from '@farris/ui-notify';
import { LanguageService } from './languag.service';
/**
 * 通知服务
 * @scope FormModule
 * @description
 * 消息提示使用策略：成功使用success，失败使用error，警告使用warning，其他场景使用info
 */
declare class FormNotifyService {
    private notifyService;
    private languageService;
    /**
     * 构造函数
     * 注入@farris/ui的NotifyService
     */
    constructor(notifyService: NotifyService, languageService: LanguageService);
    /**
     * 信息提示
     * @param content 内容
     */
    default(content: string): void;
    /**
     * 信息提示
     * @param content 内容
     */
    info(content: string, options?: {
        [prop: string]: any;
    }): void;
    /**
     * 成功提示
     * @param content 内容
     * @param options? 选项 {shorTitle: false}
     */
    success(content: string, options?: {
        [prop: string]: any;
    }): void;
    /**
     * 警告提示
     * @param content 内容
     * @param options 选项
     */
    warning(content: string, options?: {
        [prop: string]: any;
    }): void;
    /**
     * 错误提示
     * @param content 内容
     */
    error(content: string, options?: {
        [prop: string]: any;
    }): void;
}
export { FormNotifyService };
