/**
 * 变更相关定义
 * @author Witt<jiwt@inspur.com>
 */
/**
 * 绑定数据变更
 */
export interface Change {
    /**
     * 变更类型
     */
    type: ChangeType;
    /**
     * 变更路径
     */
    path: string[];
    /**
     * 变更后的值
     */
    value?: any;
    /**
     * 变更前的值
     */
    preValue?: any;
    /**
     * 错误信息
     */
    errors?: any;
    /**
     * 验证后回调，会将验证结果传入
     */
    cb?: Function;
    /**
     * 是否提交过，为true时开启输入时验证
     */
    isSubmitted?: boolean;
    /**
     * 在grid中使用，如果存在id，则将errors在form中以
     * {
     *  [id]: errors
     * }
     * 的形式保存一份
     */
    id?: string;
    isUdt?: boolean;
    isGrid?: boolean;
    /**
     * 新增
     */
    create?: boolean;
    /**
     * 复制
     */
    isCloned?: boolean;
    /**
     * 是否分层加载场景
     */
    isTreeNodeLoadScene?: boolean;
    /**
     * 详细信息
     */
    detail?: {
        [prop: string]: any;
    };
    /**
     * 经过bindingList转发
     */
    isBindingListTransmited?: boolean;
    [prop: string]: any;
}
/**
 * 绑定数据变更类型
 */
export declare enum ChangeType {
    Update = "Update",
    Load = "Load",
    Append = "Append",
    Remove = "Remove",
    Swap = "Swap",
    SelectionChanged = "SelectionChanged",
    ValueChanged = "ValueChanged",
    UpdateErrors = "UpdateErrors",
    GlobalSelectionChanged = "GlobalSelectionChanged",
    /**
     * 分页信息变化
     */
    PaginationInfoChange = "PaginationInfoChange"
}
/**
 * 视图变更
 */
export interface ViewChange {
    type: ViewChangeType;
    path: string[];
    value: any;
    preValue?: any;
    context?: any;
    [prop: string]: any;
}
/**
 * 视图变更类型
 */
export declare enum ViewChangeType {
    ValueChanged = 0
}
/**
 * Form值变化事件
 */
export interface FormValueChange {
    path: string[];
    value: any;
    preValue?: any;
    command: string;
    entityChanged: boolean;
}
/**
 * 实体值变化事件
 */
export interface EntityValueChange {
    paths: string[];
    value: any;
    preValue?: any;
    changed: boolean;
    [prop: string]: any;
}
