import { Observable } from "rxjs";
/**
 * 分页信息
 */
export interface Pagination {
    /**
     * 当前页码
     */
    pageIndex: number;
    /**
     * 分页大小
     */
    pageSize: number;
    /**
     * 总记录数
     */
    totalRecords: number;
    /**
     * 总页数
     */
    totalPages: number;
}
/**
 * 绑定对象值变化处理器
 */
export interface InvokeOnValueChange {
    (preValue: any, value: any, entityChanged: boolean, primaryValue?: any): Observable<boolean>;
}
