export interface CommandController {
    id: string;
    code: string;
    name: string;
    methods: {
        [methodName: string]: ControllerMethod;
    };
}
export interface ControllerMethod {
    id: string;
    code: string;
    name: string;
    params: MethodParam[];
    stages: MethodStage[];
}
export interface MethodParam {
    name: string;
    type: string;
    expression: string;
}
export interface MethodStage {
    /**
     * executing:可执行节点；fork:分支节点；determing:条件节点
     */
    type: 'executing' | 'fork' | 'determing';
    name: string;
}
export interface ExecutingStage extends MethodStage {
    source: string;
    service: string;
    method: string;
    params: StageParam[];
}
export interface ForkStage extends MethodStage {
    stages: DeterminingStage[];
}
export interface DeterminingStage extends MethodStage {
    name: string;
    conditonType: number;
    condition: string;
    stages: MethodStage[];
}
export interface StageParam {
    name: string;
    expression: string;
}
