/**
 * 路径类型
 */
declare enum DataPathNodeType {
    /**
     * 标记该节点是一个实体主键值，用来在列表上定位一个实体
     */
    DataId = "DataId",
    /**
     * 标记该节点是一个属性名，用来在对象上定位一个属性
     */
    PropName = "PropName"
}
/**
 * 路径节点
 */
declare class DataPathNode {
    /**
     * 上一节点
     */
    prev: DataPathNode;
    /**
     * 下一节点
     */
    next: DataPathNode;
    /**
     * 节点类型
     */
    type: DataPathNodeType;
    /**
     * 路径数据
     * 对于List类型：  value是主键值，用于指明具体是哪一行；
     * 对于Object类型：value是属性名，用于指明具体哪一属性。
     */
    value: any;
    /**
     * 构造函数
     */
    constructor(type: DataPathNodeType, data: any);
}
export { DataPathNodeType, DataPathNode };
