import { DataTypeInfo } from './data_type_info';
/**
 * 实体属性分组
 */
declare enum DataPropGroup {
    /**
     * 简单类型
     */
    Plain = "Plain",
    /**
     * 实体类型
     */
    Object = "Object",
    /**
     * 动态实体类型
     */
    Dynamic = "Dynamic",
    /**
     * 列表类型
     */
    List = "List"
}
/**
 * 实体属性信息
 */
declare class DataPropInfo {
    /**
     * 属性类型
     */
    group: DataPropGroup;
    /**
     * 属性名称
     */
    name: string;
    /**
     * 影射名称
     */
    mapping: string;
    /**
     * 属性类型描述
     */
    typeInfo: DataTypeInfo;
    /**
     * 元数据信息
     * @todo
     * 1、EntityPropInfo不应该认识元数据描述，不能强识别元数据上的属性；
     * 2、将来元数据可能有多套，每一套有自己的解析 元数据解析框架还没有做，所以临时处理，不对暴露
     */
    metadataInfo?: any;
    /**
     * 是否vo字段
     */
    isVOField?: boolean;
}
export { DataPropGroup, DataPropInfo };
