import { FrameContext } from '../../frame/index';
import { ValidationError } from './validation_error';
/**
 * 验证规则
 */
export interface ValidateRule {
    /**
     * 验证类型
     */
    type: string;
    /**
     * 验证参数
     */
    constraints?: any[];
    /**
     * 提示信息
     */
    message?: string;
    /**
     * 属性名称
     */
    property?: string;
    /**
     * 所属实体类型名称
     */
    targetName?: string;
    /**
     * 验证目标标识
     */
    targetId?: string;
    /**
     * 表达式Id
     */
    expressionId?: string;
    /**
     * 自定义校验方法
     */
    eval?: (options?: any) => any;
    /**
     * 字段名
     */
    field?: string;
    /**
     * 绑定路径(不包含字段路径)
     */
    bindingPath?: string;
    /**
     * 字段绑定路径
     */
    binding?: string;
    frameContext?: FrameContext;
    /**
     * 完整路径(带bindingPath及字段路径)
     * @description 等于bindingPath + binding
     */
    fullPath?: string;
}
export interface Validation {
    targetId: string;
    targetName: string;
    property: string;
    rules: ValidateRule[];
}
/**
 * 校验数字选项
 */
export interface IsNumberOptions {
    /**
     * 无穷大的值是否为数字
     */
    allowInfinity?: boolean;
    /**
     * NaN 时判断是否为数字
     */
    allowNaN?: boolean;
}
/**
 * 实体校验结果
 */
export interface ValidationResult {
    /**
     * 是否验证通过
     * true: 验证通过
     * false: 未通过验证
     */
    isValid: boolean;
    /**
     * 验证错误集合
     */
    errors: ValidationError[];
    /**
     * 验证错误信息
     */
    message?: string;
}
