/**
 * 数据路径处理
 */
declare class BindingPathConverter {
    /**
     * (BindingPathString | BindingPathArray) => BindingPathArray
     * @param bindingPath BindingPath的字符串或者数组格式
     * @return BindingPath数组
     */
    static toBindingPathArray(bindingPath: string | string[]): string[];
    /**
     * BindingPathArray => BindingPathString
     */
    static toBindingPathString(bindingPathArray: string[]): string;
}
export { BindingPathConverter };
