import { InjectionToken } from '@angular/core';
/**
 * 变量解析接口
 */
interface VariableParser {
    /**
     * 解析变量
     * @param expression 表达式
     * @param context 上下文
     */
    parse(expression: string, context: any, eventParam?: any): any;
}
declare const VARIABLE_PARSERS: InjectionToken<VariableParser>;
export { VariableParser, VARIABLE_PARSERS };
