import { InjectionToken } from '@farris/mobile-devkit';
import { IErrorServe } from './types';
declare const BE_SERVER_URI_TOKEN: InjectionToken<string>;
/**
 * 1、因为bef里，很多类都没有注入，BefSessionHandlingStragegy无法直接注入；
 * 2、通过一个字符串Token来间接做策略选择。
 */
