import { Type } from '../core/index';
import { Entity } from '../entity/index';
import { BindingProperty } from './binding_property';
/**
 * 属性工具类
 */
declare class PropertyUtil {
    /**
     * 获取实体上的属性集合，并将他们转换成BindingProperty集合
     * @param  entityType 实体类型
     * @returns 绑定属性集合
     */
    static getProperties(entityType: Type<Entity>): BindingProperty[];
    static getDynamicProperties(dynamicData: any): BindingProperty[];
    /**
     * 根据属性名获取属性
     */
    static getPropertyByName(properties: BindingProperty[], propertyName: string): BindingProperty;
    /**
     * 获取实体主键名
     * @param properties 属性集合
     * @returns 主键名
     */
    static getPrimaryKey(properties: BindingProperty[]): string;
}
export { PropertyUtil };
