/**
 * 命令处理扩展注册器相关定义
 * @author Witt<jiwt@inspur.com>
 */
import { Injector } from '../core/index';
import { CommandHandlerExtender } from './command_handler_extender';
/**
 * 命令处理扩展注册器
 */
declare class CommandHandlerExtenderRegistry {
    private injector;
    /**
     * CommandHandlerExtender实例Map
     * - key：命令名称
     * - value：命令扩展实例
     */
    private extendersMap;
    /**
     * 构造函数
     * @param extenders 命令扩展实例数组
     */
    constructor(injector: Injector);
    /**
     * 获取命令扩展实例数组
     * @param   commandName 命令名称
     * @returns 命令处理扩展实例数组
     */
    get(commandName: string): CommandHandlerExtender[];
    /**
     * 添加命令扩展
     * @param commandName Command名称
     * @param extender    CommandHandlerExtender实例
     * @return void
     */
    set(commandName: string, extender: CommandHandlerExtender): void;
    /**
     * 注册命令扩展
     * @param extender CommandHandlerExtender实例
     */
    regist(extender: CommandHandlerExtender): void;
}
export { CommandHandlerExtenderRegistry };
