import { Observable } from "rxjs";
/**
 * 表达式资源清单描述
 */
export interface FormExpressionsManifest {
    path: string;
    ns: string;
}
/**
 * 表单清单
 */
export interface FormManifest {
    expressions: Array<FormExpressionsManifest>;
}
/**
 * 表单清单加载服务
 */
export interface IFormManifestService {
    load(): Observable<FormManifest>;
}
/**
 * 表达式加载服务
 */
export interface IFormExpressionManifestService {
    load(): Observable<Array<any> | null>;
}
