/**
 * 绑定值转换器
 */
interface BindingValueConverter {
    /**
     * 将ControlValue转换为StateValue值
     */
    convertFrom(controlValue: any): any;
    /**
     * 将StateValue转换为ControlValue
     */
    convertTo(stateValue: any): any;
}
/**
 * 日期字符串转换器
 */
declare class DateStringValueConverter implements BindingValueConverter {
    convertFrom(dateObj: Date): string;
    convertTo(dateString: any): Date;
}
/**
 * 数组字符串转换器
 */
declare class ArrayStringValueConverter implements BindingValueConverter {
    convertFrom(arr: any[]): string;
    convertTo(arrString: any): any[];
}
export { BindingValueConverter, DateStringValueConverter, ArrayStringValueConverter };
