/**
 * 零代码状态工具类
 */
declare class RenderStateUtil {
    /**
     * 制单状态
     * @sumamry
     * 1、发起流程中通过菜单打开；
     * 2、草稿箱列表打开；
     */
    static getRenderStatesWhenDrafting(): NoCodeRenderState;
    /**
     * 撤回重新制单
     * @summary
     * 我的发起中打开已撤回并且未重新提交的单据
     */
    static getRenderStatesWhenReDrafting(): NoCodeRenderState;
    /**
     * 驳回修改
     * @summary
     * 1、我的代办中共打开已驳回的单据；
     */
    static getRenderStatesWhenBackEdit(): NoCodeRenderState;
    /**
     * 审批中
     * @sumamry
     * 1、我的代办中：未被驳回的单据；
     * 2、默认为只读态，最终以流程节点上的配置为准
     */
    static getRenderStatesWhenApproving(): NoCodeRenderState;
    /**
     * 仅查看
     */
    static getRenderStatesWhenViewOnly(): NoCodeRenderState;
}
/**
 * 零代码控件状态
 */
export interface NoCodeRenderState {
    showHeader: boolean;
    showFooter: boolean;
    showChildHeader: boolean;
    showChildFooter: boolean;
}
export { RenderStateUtil as NoCodeRenderStateUtil };
