/**
 * 验证规则
 */
export interface ValidationRule {
    /**
     * 验证类型
     */
    type: string;
    /**
     * 验证参数
     */
    constraints?: any[];
    /**
     * 提示信息
     */
    message?: string;
    /**
     * 属性名称
     */
    property?: string;
    /**
     * 所属实体类型名称
     */
    targetName?: string;
    /**
     * 验证目标标识
     */
    targetId?: string;
}
