styles:
.table td:focus-within {
    box-shadow: inset 0 0 0 2px rgba(0, 0, 0, 0.13);
}
.table td div {
    width: 100%;
    height: 100%;
}
.table td input {
    width: 100%;
    height: 100%;
    font-family: auto;
    line-height: 1;
    font-size: 13px;
}
.table td input:focus {
    border: 0;
}
input[type='checkbox']:focus {
    outline: none;
}

.f-table-norecords-row:hover {
    background-color: transparent!important;
    cursor: default!important;
}
