export interface PaginationInstance {
    /**
     * An optional ID for the pagination instance. Only useful if you wish to
     * have more than once instance at a time in a given component.
     */
    id?: string;
    /**
     * 每页记录数.
     */
    itemsPerPage: number;
    /**
     * 当前页码
     */
    currentPage: number;
    /**
     * 总记录数
     */
    totalItems?: number;
    /** 每页显示记录数 */
    pageList: number[];
    /** 启用服务器端分页 */
    remote?: boolean;
}
