export interface TreeNode {
    id?: string;
    label?: string;
    data?: any;
    /** 叶子节点图标 */
    icon?: any;
    /** 节点展开图标 */
    expandedIcon?: any;
    /** 节点折叠图标 */
    collapsedIcon?: any;
    /** 显示图标 */
    showIcon?: boolean;
    /** 子节点 */
    children?: TreeNode[];
    /** 是否为叶子节点 */
    leaf?: boolean;
    /** 是否展开 */
    expanded?: boolean;
    type?: string;
    /** 父节点 */
    parent?: TreeNode;
    partialSelected?: boolean;
    styleClass?: string;
    draggable?: boolean;
    droppable?: boolean;
    /** 是否允许选中 */
    selectable?: boolean;
    /** 启用节点级分页加载，默认为 false */
    usePagination?: boolean;
    pagination?: {
        pageSize: number;
        pageIndex: number;
        total: number;
    };
    __childs_loading__?: boolean;
}
export interface RowNode {
    id: string;
    node: TreeNode;
    parent: TreeNode;
    parentRowNode?: RowNode;
    level: number;
    visible: boolean;
    isSelected?: boolean;
    isChecked?: boolean;
    indeterminate?: boolean;
    parents?: string[];
    index?: number;
    last?: boolean;
    first?: boolean;
}
export interface SelectEventArgs {
    originalEvent: Event;
    rowNode: RowNode;
    rowIndex: number;
}
