import { VMParameterType } from '../ParaEnum/VMParameterType';
import { VMParameterMode } from '../ParaEnum/VMParameterMode';
import { VMCollectionParameterType } from '../ParaEnum/VMCollectionParameterType';
/**
 * Action执行参数
 * @author wangjiegj<wangjiegj@inspur.com>
 */
/**
 * Action执行参数
 */
export interface IViewModelParameter {
    /**
     * 唯一标志
     */
    ID: string;
    /**
     * 编号
     */
    ParamCode: string;
    /**
     * 名称
     */
    ParamName: string;
    /**
     * 参数类型
     */
    ParameterType: VMParameterType;
    /**
     * 程序集名
     */
    Assembly: string;
    /**
     * 类名
     */
    ClassName: string;
    JavaClassName: string;
    /**
     * 参数模式
     */
    Mode: VMParameterMode;
    /**
     * 描述
     */
    ParamDescription: string;
    /**
     * 参数类型
     */
    CollectionParameterType: VMCollectionParameterType;
}
