import { ViewModelActionType } from '../Enum/ViewModelActionType';
import { ViewModelMapping } from '../Common/ViewModelMapping';
import { ViewModelReturnValue } from './ReturnValue/ViewModelReturnValue';
import { ViewModelParameter } from './Para/ViewModelParameter';
/**
 * VM Action抽象类
 * @author wangjiegj<wangjiegj@inspur.com>
 */
/**
 * VM Action抽象类
 */
export declare abstract class ViewModelAction {
    constructor();
    /**
     * 唯一标志
     */
    ID: string;
    /**
     * 编号
     */
    Code: string;
    /**
     * 名称
     */
    Name: string;
    /**
     * Action所在的节点
     */
    /**
     * 类型
     */
    abstract Type: ViewModelActionType;
    /**
     * 形参集合
     */
    ParameterCollection: ViewModelParameter[];
    /**
     * 返回值
     */
    ReturnValue: ViewModelReturnValue;
    /**
     * 操作映射
     */
    Mapping: ViewModelMapping;
    ExtendProperties: any;
    /**
     * 构件名称
     */
    ComponentName: string;
    /**
     * 是否自动保存
     */
    IsAutoSave: boolean;
}
