import { CommonFilter } from './common-filter';
export interface GspUserFilter extends CommonFilter {
    orgId?: string;
    /**
     * 排序字段
     */
    orderField?: string;
    queryMode?: string;
    /**
     * 排序规则
     */
    orderType?: string;
    /** 过滤当前登录用户 */
    notGetCurrentUser?: boolean;
    /** 过滤停用用户 */
    notGetStopUser?: boolean;
    /**true为获取启用用户，false为获取停用用户，默认为true */
    enableUser?: string;
    /** 岗位ID，根据岗位ID过滤用户 */
    posId?: string;
}
