import { ComponentFactory } from '@angular/core';
import { TaskAction } from '../entity/task-action.entity';
import { Observable } from 'rxjs';
import { TaskEntity } from '../entity';
import { ActionBaseImplComponent } from '../action-base-impl/action-base-impl.component';
export interface TaskActionImpl {
    /**
     * 第三方模块需实现返回动作组件factory接口
     */
    getActionComponentFactory(action: TaskAction): ComponentFactory<ActionBaseImplComponent>;
    /**
     * 第三方存在联查动作时,实现返回表单Url 的接口
     */
    getFormUrl(task: TaskEntity, linkAction: TaskAction): Observable<string>;
    /**
     * 第三方存在查看动作时（譬如查看影像，查看流程图等）时，需实现本接口
     */
    excute(task: TaskEntity, linkAction: TaskAction): Observable<boolean>;
}
