import { RegisteredWidgetConfig } from '../../types';
/** 存在 window 上为了避免 ng serve 和 farris rollup 无法一致单例性 */
declare global {
    interface Window {
        /** 单独存 widget base 的 properties，因为它不是个元部件 */
        registry: {
            [widgetName: string]: RegisteredWidgetConfig;
        };
    }
}
/**
 * 元部件注册表
 */
export declare const WidgetRegistry: {
    setWidget(name: string, config: RegisteredWidgetConfig): void;
    getWidgetByName(name: string): RegisteredWidgetConfig;
    getWidgetByCtorName(ctorName: string): RegisteredWidgetConfig;
};
